import axios from 'axios';


const API_URL = `${process.env.REACT_APP_CONFIG_API_SERVER}/api/hacker_news/top_10_stories/`;

export const fetchTopStoriesWithFetch = async () => {
    try {
        const response = await fetch(`${API_URL}/api/hacker_news/top_10_stories/`);
        if (!response.ok) {
            throw new Error('Network response was not ok');
        }
        const data = await response.json();
        return data;
    } catch (error) {
        console.error("Error fetching top stories:", error);
        throw error;
    }
};
